<script>
	import { gsap } from 'gsap';
	import { DrawSVGPlugin } from "gsap/DrawSVGPlugin.js";
	gsap.registerPlugin(DrawSVGPlugin);
	export default {
		methods: {
			viewCharacter(character) {
				Event.$emit('select-character', character);
			}
		},
		mounted() {			

			var tl = gsap.timeline({ delay: 1.5 });

			gsap.set('.line2', { y: -38 });
			gsap.set('.bigText', { autoAlpha: 0});
			gsap.set('.character', { autoAlpha: 0 });

			gsap.to('.character', {
					duration: 3, 
					autoAlpha: 1, 
					ease: "power2.inOut", 
					stagger: { 
						amount: 1, 
						from: "random", 
						grid: "auto"
					}
			})

			tl.to('.rectangle', {duration: .5, ease: "circ.inOut", "clip-path": "polygon(100% 0, 0 0, 0 100%, 100% 100%)" })
			.to('.bigText', {duration: .75, autoAlpha: 1 })
			.from('.line2', { duration: .5, drawSVG: "0% 0%", ease: "power3.inOut" })
			.from('.line1', { duration: .5, drawSVG: "0% 0%", ease: "power3.inOut" }, "-=.75")
			.to('.line2', { duration: .5, y: 0 })
			.to('.littleText', {duration: .5, "clip-path": "polygon(0 0, 100% 0, 100% 100%, 0 100%)"}, "-=.4");
			
		}
	}
</script>

